INSERT INTO movie(id, name,	release_date, duration, budget,	description, countries, genres)
		VALUES (1, 'Я - легенда', TO_DATE('05-12-2007', 'DD-MM-YYYY'), 96, 150000000, 'Адаптация одноименного романа Ричарда Мэтисона о неизвестном вирусе, унесшем жизни половины населения земного шара, а остальную половину превратившего в вампиров. Сюжет строится вокруг единственного уцелевшего человека с необъяснимым иммунитетом, ночами держащего бесконечную осаду упырей, а днем пытающегося найти противоядие и выяснить причины эпидемии.', 'Россия', 'комедия');
INSERT INTO movie(id, name,	release_date, duration, budget,	description, countries, genres)
		VALUES (2, 'Джанго освобожденный', TO_DATE('25-12-2012', 'DD-MM-YYYY'), 165, 100000000, 'Эксцентричный охотник за головами, также известный как «Дантист», промышляет отстрелом самых опасных преступников. Работенка пыльная, и без надежного помощника ему не обойтись. Но как найти такого и желательно не очень дорогого? Беглый раб по имени Джанго — прекрасная кандидатура. Правда, у нового помощника свои мотивы — кое с чем надо разобраться…', 'Россия', 'комедия');	
INSERT INTO movie(id, name,	release_date, duration, budget,	description, countries, genres)
		VALUES (3, 'Волк с Уолл-Стрит', TO_DATE('17-12-2013', 'DD-MM-YYYY'), 180, 100000000, 'Джордан Белфорт основал одну из крупнейших брокерских контор в 1987 году, но десять лет спустя был осужден за отмывание денег и ряд прочих финансовых преступлений. Он справился с алкогольной и наркотической зависимостью, выработанной за время махинаций на Уолл-стрит, написал две книги и теперь читает лекции о том, как достичь успеха.', 'Россия', 'комедия');
INSERT INTO movie(id, name,	release_date, duration, budget,	description, countries, genres)
		VALUES (4, 'После нашей эры', TO_DATE('01-05-2013', 'DD-MM-YYYY'), 100, 130000000, 'События разворачиваются через тысячу лет после катастрофы, вынудившей человечество покинуть Землю. Новым домом становится планета под названием Нова Прайм. Легендарный генерал Сайфер Рейдж возвращается с очередного боевого задания в семью, которая раньше обходилась без его родительского внимания, чтобы стать отцом своему 13-летнему сыну Китаю. Во время астероидной бури летательный аппарат с папой и сыном на борту терпит крушение, падая на незнакомую и опасную Землю. И пока Рейдж-старший на последнем дыхании лежит среди обломков своего корабля, сын должен пересечь враждебный ландшафт, чтобы запустить их спасательный маячок. Ни о чем большем юноша в своей жизни не мечтал: быть солдатом, как отец, — его заветное желание. Сегодня у него есть шанс проявить себя.', 'Россия', 'комедия');
INSERT INTO movie(id, name,	release_date, duration, budget,	description, countries, genres)
		VALUES (5, 'Титаник', TO_DATE('01-11-1997', 'DD-MM-YYYY'), 194, 200000000, 'Молодые влюбленные Джек и Роза находят друг друга в первом и последнем плавании «непотопляемого» Титаника. Они не могли знать, что шикарный лайнер столкнется с айсбергом в холодных водах Северной Атлантики, и их страстная любовь превратится в схватку со смертью…', 'Россия', 'комедия');
INSERT INTO movie(id, name,	release_date, duration, budget,	description, countries, genres)
		VALUES (6, 'Начало', TO_DATE('08-07-2010', 'DD-MM-YYYY'), 148, 160000000, 'Кобб — талантливый вор, лучший из лучших в опасном искусстве извлечения: он крадет ценные секреты из глубин подсознания во время сна, когда человеческий разум наиболее уязвим. Редкие способности Кобба сделали его ценным игроком в привычном к предательству мире промышленного шпионажа, но они же превратили его в извечного беглеца и лишили всего, что он когда-либо любил. И вот у Кобба появляется шанс исправить ошибки. Его последнее дело может вернуть все назад, но для этого ему нужно совершить невозможное — инициацию. Вместо идеальной кражи Кобб и его команда спецов должны будут провернуть обратное. Теперь их задача — не украсть идею, а внедрить ее. Если у них получится, это и станет идеальным преступлением. Но никакое планирование или мастерство не могут подготовить команду к встрече с опасным противником, который, кажется, предугадывает каждый их ход. Врагом, увидеть которого мог бы лишь Кобб.', 'Россия', 'комедия');
INSERT INTO movie(id, name,	release_date, duration, budget,	description, countries, genres)
		VALUES (7, 'Поймай меня, если сможешь', TO_DATE('16-12-2002', 'DD-MM-YYYY'), 141, 52000000, 'Фрэнк Эбегнейл успел поработать врачом, адвокатом и пилотом на пассажирской авиалинии — и все это до достижения полного совершеннолетия в 21 год. Мастер в обмане и жульничестве, он также обладал искусством подделки документов, что в конечном счете принесло ему миллионы долларов, которые он получил по фальшивым чекам.Агент ФБР Карл Хэнрэтти отдал бы все, чтобы схватить Фрэнка и привлечь к ответственности за свои деяния, но Фрэнк всегда опережает его на шаг, заставляя продолжать погоню.', 'Россия', 'комедия');
	
